def main():
    print('Welcome to the {{ project_name }} Python package!')

if __name__ == '__main__':
    main()

from setuptools import setup, find_packages

setup(
    name='{{ project_name }}',
    version='0.1',
    packages=find_packages(),
    install_requires=[],
    entry_points={
        'console_scripts': [
            '{{ project_name }} = {{ project_name }}.main:main',
        ],
    },
)
